$(function(){
$('form#info .slider label').each(function(){
	var labelColor = '#999';
	var restingPosition = '5px';

	// придаем стиль ярлыку с помощью JS progressive enhancement
	$(this).css({
		'color' : labelColor,
		 	'position' : 'absolute',
	 		'top' : '6px',
			'left' : restingPosition,
			'display' : 'inline',
    	               'z-index' : '99'
	});

	// берем значение ввода
	var inputval = $(this).next('input').val();

	// берем ширину ярлыка, и добавляем к ней 5 пикселей
	var labelwidth = $(this).width();
	var labelmove = labelwidth + 5;

	//onload, проверить заполнено ли поле, если да, передвинуть ярлык
	if(inputval !== ''){
		$(this).stop().animate({ 'left':'-'+labelmove }, 1);
	}    	

	// если поле пустое с фокусом - передвинуть ярлык влево
	// Если пустое без фокуса, вернуть обратно
	$('input').focus(function(){
		var label = $(this).prev('label');
		var width = $(label).width();
		var adjust = width + 5;
		var value = $(this).val();

		if(value == ''){
			label.stop().animate({ 'left':'-'+adjust }, 'fast');
		} else {
			label.css({ 'left':'-'+adjust });
		}
	}).blur(function(){
		var label = $(this).prev('label');
		var value = $(this).val();
		if(value == ''){
			label.stop().animate({ 'left':restingPosition }, 'fast');
		}
	});
})
});
